const ConsoleLogLogger = require('./models/console-log-logger')
const WinstonLogger = require('./models/winston-logger')

module.exports.create = (loggerType) => {

    if(loggerType === "console-log-logger") {
        return new ConsoleLogLogger();
    }
    if (loggerType === "winston-logger") {
        return new WinstonLogger();
    }
    
}