const Log = require('./Log');
const ErrorDetail = require('./errorDetail');

class ErrorLog extends Log{
    constructor() {
        super();
        this.details = undefined;
    }

    withDetails (action, code, message) {
        let errorDetail = new ErrorDetail(action, code, message);
        this.details = errorDetail;
        return this;
    }
}

module.exports = ErrorLog;
